  `ID` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `DocType` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文档类型 1.NDA协议文档，2.其它主文档，3.文档附件，4.其它文档',
