-- ----------------------------
-- Table structure for tbl_ndabasicinfo
-- ----------------------------
DROP TABLE IF EXISTS `tbl_ndabasicinfo`;
CREATE TABLE `tbl_ndabasicinfo`  (
  `ID` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL DEFAULT '1' COMMENT 'ID 同一个文档的不同历史版本公用此ID',
  `Title` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '标题',
  `AbstractContext` varchar(3072) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '摘要',
  `KeyWords` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '关键词',
  `FileName` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文件名 本文档所属NDA',
  `FileExtension` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '扩展名',
  `FileHash` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT 'NDA文件Hash',
  `TimeStamp` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '时间戳',
  `DocTypeID` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文档类型 tbl_DictDocTypes中的文档类型ID',
  `DocOrder` int(11) NULL DEFAULT NULL COMMENT '文档顺序 此文档在NDA所有文档中按照上传时间的排序，避免每次显示顺序变化',
  `InitiatorUserName` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '发起人 创建人在tbl_User中的UserName',
  `InitiatorTime` datetime NOT NULL COMMENT '发起时间',
  `InitiatorIP` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '发起IP',
  `UpdateUserName` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '最近更新人',
  `UpdateTime` datetime NULL DEFAULT NULL COMMENT '最近更新时间 最后一次更新时间',
  `LastDocNo` int(11) NULL DEFAULT 0 COMMENT '最新文档序号 本NDA最新文档序号，每增加一个新文档，本字段加1',
  `PubKey` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '公钥',
  `PrivateKey` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '私钥',
  `NDAItems` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT 'NDA条款 NDA条款文本',
  `memo` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '备注',
  `Revision` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '乐观锁 为了解决并发冲突增加的字段',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = 'tbl_NDABasicInfo 某一NDA的基本信息表，后续的文档以此表为发起点' ROW_FORMAT = Compact;

