DROP TABLE IF EXISTS `tbl_ndadocinfo`;
CREATE TABLE `tbl_ndadocinfo`  (
  `ID` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `NDADocID` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT 'NDA文档ID tbl_NDADocBasicInfo中的ID',
  `DocHash` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '文档Hash 文档内容本身的Hash，用于在IPFS中检索',
  `TimeStamp` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '时间戳 利用文档Hash、时间等生成的Hash',
  `FileName` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '文件名',
  `FileExtension` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '扩展名',
  `FileOrder` int(11) NULL DEFAULT 1 COMMENT '顺序 按照上传时间对文档进行编号排序',
  `UploadUserName` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '上传人',
  `UploadTime` datetime NULL DEFAULT NULL COMMENT '上传时间',
  `UploadIP` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '上传IP',
  `PrevID` int(11) NULL DEFAULT NULL COMMENT '前一文档ID 此表中的ID',
  `PrevTimeStamp` varchar(1024) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '前一文档时间戳',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 17 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = 'tbl_NDADocInfo NDA文档记录信息表，这里的文档是指除了NDA协议本身外的其它文档。每增加一个NDA文档，在此表中增加一条新记录。文档时间戳由文档内容hash值+其它信息生成。按照现在的思路，文档的内容存放在IPFS文件系统中，文档Hash在保存时返回。除此以外，要保存上一文档的时间戳' ROW_FORMAT = Compact;

